plugins {
    id 'java'
    id 'org.springframework.boot' version '3.5.0'
    id 'io.spring.dependency-management' version '1.1.5'
    id 'com.diffplug.spotless' version '7.0.4'
    id 'org.openapi.generator' version '7.3.0'
    id 'pmd'
}

group = 'io.finhq'
version = ''

java {
    sourceCompatibility = JavaVersion.VERSION_21
    targetCompatibility = JavaVersion.VERSION_21
}

repositories {
    mavenCentral()
}

dependencies {
    implementation 'io.finhq.commons.java:finhq-errors'
    implementation 'io.finhq.commons.java:finhq-helpers'
    implementation 'io.finhq.commons.java:finhq-context'
    implementation 'io.finhq.openapi:finhq-openapi-java'
    implementation 'io.finhq.commons.spring:finhq-spring'
    implementation 'io.finhq.commons.spring:finhq-entity'

    implementation 'org.springframework.boot:spring-boot-starter-web'
    implementation 'org.springframework.boot:spring-boot-starter-actuator'
    implementation 'org.springframework.boot:spring-boot-starter-validation'
    implementation 'org.springframework.boot:spring-boot-starter-data-jpa'

    implementation 'io.swagger:swagger-annotations:1.6.12'
    implementation 'org.springdoc:springdoc-openapi-starter-webmvc-ui:2.2.0'

    implementation 'com.fasterxml.jackson.datatype:jackson-datatype-jsr310:2.15.3'
    implementation 'com.fasterxml.jackson.datatype:jackson-datatype-jdk8:2.15.3'
    implementation 'org.openapitools:jackson-databind-nullable:0.2.6'

    // JSONObject and JSON Schema dependencies
    implementation 'com.networknt:json-schema-validator:1.0.83'
    implementation 'com.fasterxml.jackson.core:jackson-databind:2.17.1'
    implementation 'org.json:json:20230227'

    // Lombok for reducing boilerplate code
    compileOnly 'org.projectlombok:lombok:1.18.30'
    annotationProcessor 'org.projectlombok:lombok:1.18.30'
    testCompileOnly 'org.projectlombok:lombok:1.18.30'
    testAnnotationProcessor 'org.projectlombok:lombok:1.18.30'

    // Database
    runtimeOnly 'com.mysql:mysql-connector-j'

    // Flyway for database migrations
    implementation 'org.flywaydb:flyway-core'
    implementation 'org.flywaydb:flyway-mysql'

    // Development only dependencies
    developmentOnly 'org.springframework.boot:spring-boot-devtools'

    testImplementation 'org.springframework.boot:spring-boot-starter-test'
}

def openApiSpecs = [
    [
        name: "configuration",
        path: "${rootDir}/../finhq-openapi/specs/configuration.yaml"
    ]
]

def generateTasks = []

openApiSpecs.each { spec ->
    def taskName = "generate${spec.name.capitalize()}Api"
    generateTasks << taskName

    task "$taskName"(type: org.openapitools.generator.gradle.plugin.tasks.GenerateTask) {
        generatorName = "spring"
        inputSpec = spec.path
        outputDir = "${buildDir}/generated"
        apiPackage = "io.finhq.configuration.openapi.api"
        modelPackage = "io.finhq.openapi.model"
        configOptions = [
            dateLibrary: "java8",
            delegatePattern: "true",
            interfaceOnly: "false",
            apiSuffix: "Api",
            useSpringBoot3: "true",
            useJakartaEe: "true",
            openApiNullable: "false",
            useTags: "true",
            documentationProvider: "springdoc",
            serializationLibrary: "jackson",
            hideGenerationTimestamp: "true",
            useBeanValidation: "true",
            useOptional: "false",
            implicitHeaders: "true",
            swaggerAnnotations: "false",
            skipDefaultInterface: "false",
            returnSuccessCode: "true",
            useAnnotatedBaseName: "true",
            generateOperationBody: "true",
            useResponseEntity: "false"
        ]
        globalProperties = [
            "apiDocs": "false",
            "modelDocs": "false",
            "models": "false",
            "apis": "",
            "apiTests": "false",
            "modelTests": "false",
            "supportingFiles": ""
        ]
    }
}

compileJava.dependsOn generateTasks

tasks.withType(Test) {
    useJUnitPlatform()
}

bootJar {
    archiveFileName = "${project.name}.jar"  // Forces name without version
}


sourceSets {
    main {
        java {
            srcDir "${buildDir}/generated/src/main/java"
            srcDir "src/main/java"
        }
        resources {
            srcDir "src/main/resources"
        }
    }
    test {
        java {
            srcDir "src/test/java"
        }
        resources {
            srcDir "src/test/resources"
        }
    }
}

// Fix package structure issue
tasks.withType(JavaCompile) {
    options.compilerArgs += ["-Xlint:none"]
}

// Set duplicates strategy for all tasks that copy resources
tasks.withType(Copy) {
    duplicatesStrategy = DuplicatesStrategy.INCLUDE
}

// Set duplicates strategy for processResources task specifically
processResources {
    duplicatesStrategy = DuplicatesStrategy.INCLUDE
}

spotless {
    format 'misc', {
        target '*.gradle', '*.md', '.gitignore'
        trimTrailingWhitespace()
        leadingTabsToSpaces(4)
        endWithNewline()
    }
    java {
        targetExclude("**/build/**")
        importOrder()
        removeUnusedImports()
        palantirJavaFormat('2.40.0')
        leadingTabsToSpaces(4)
    }
}

task fixFormat(dependsOn: 'spotlessApply') {
    description = 'Runs spotlessApply to fix formatting issues'
}

// Make build task depend on spotlessApply to automatically fix formatting issues
tasks.named('build').configure {
    dependsOn 'spotlessApply'
}

// Apply common PMD configuration
apply from: "${rootDir}/../pmd-common.gradle"

springBoot {
    mainClass = 'io.finhq.configuration.ConfigurationApplication'
}

