spring:
  application:
    name: finhq-configuration
  profiles:
    active: ${PROFILES_ACTIVE:development}
  devtools:
    restart:
      enabled: ${DEVTOOLS_RESTART_ENABLED:false}
      exclude: ${DEVTOOLS_RESTART_EXCLUDE:}
      additional-paths: ${DEVTOOLS_RESTART_ADDITIONAL_PATHS:}
    livereload:
      enabled: false
    add-properties: ${DEVTOOLS_ADD_PROPERTIES:false}
  kafka:
    enabled: ${KAFKA_ENABLED:false}
    bootstrap-servers: ${KAFKA_BOOTSTRAP_SERVERS:localhost:9092}
    producer:
      timeout: ${KAFKA_PRODUCER_TIMEOUT:5000}
  # Cache configuration disabled for now
  # cache:
  #   cache-names: moduleData
  #   caffeine:
  #     spec: maximumSize=100,expireAfterWrite=5m

  # Database configuration for configuration service
  datasource:
    url: jdbc:mysql://${DB_HOST:localhost}:${DB_PORT:3306}/${DB_NAME:finhq_configuration}
    username: ${DB_USERNAME:finhq}
    password: ${DB_PASSWORD:finhqpassword}
    driver-class-name: ${DB_DRIVER:com.mysql.cj.jdbc.Driver}
    hikari:
      pool-name: ${DB_POOL_NAME:HikariCP}
      maximum-pool-size: ${DB_MAX_POOL_SIZE:10}
      minimum-idle: ${DB_MIN_IDLE:5}
      idle-timeout: ${DB_IDLE_TIMEOUT:30000}
      max-lifetime: ${DB_MAX_LIFETIME:2000000}
      connection-timeout: ${DB_CONNECTION_TIMEOUT:30000}
  sandbox-datasource:
    url: jdbc:mysql://${SANDBOX_DB_HOST:localhost}:${SANDBOX_DB_PORT:3306}/${SANDBOX_DB_NAME:finhq_configuration_sandbox}
    username: ${SANDBOX_DB_USERNAME:finhq}
    password: ${SANDBOX_DB_PASSWORD:finhqpassword}
    driver-class-name: ${SANDBOX_DB_DRIVER:com.mysql.cj.jdbc.Driver}
    hikari:
      pool-name: ${SANDBOX_DB_POOL_NAME:SandboxHikariCP}
      maximum-pool-size: ${SANDBOX_DB_MAX_POOL_SIZE:5}
      minimum-idle: ${SANDBOX_DB_MIN_IDLE:2}
      idle-timeout: ${SANDBOX_DB_IDLE_TIMEOUT:30000}
      max-lifetime: ${SANDBOX_DB_MAX_LIFETIME:2000000}
      connection-timeout: ${SANDBOX_DB_CONNECTION_TIMEOUT:30000}

  # Flyway configuration for configuration service
  flyway:
    url: jdbc:mysql://${DB_HOST:localhost}:${DB_PORT:3306}/${DB_NAME:finhq_configuration}
    user: ${FLYWAY_USER:finhq}
    password: ${FLYWAY_PASSWORD:finhqpassword}
    locations: classpath:db/migration
    baseline-on-migrate: true

  # JPA Configuration
  jpa:
    open-in-view: false
    hibernate:
      ddl-auto: ${JPA_DDL_AUTO:none} # none, validate, update, create, create-drop
    properties:
      hibernate:
        format_sql: ${JPA_FORMAT_SQL:true}
        show_sql: ${JPA_SHOW_SQL:false}
        use_sql_comments: ${JPA_USE_SQL_COMMENTS:false}

server:
  port: ${SERVER_PORT:7084}

management:
  endpoints:
    web:
      exposure:
        include: health,info,metrics,prometheus
  prometheus:
    metrics:
      export:
        enabled: true
  endpoint:
    health:
      show-details: always

# Application configuration
finhq:
  commons:
    spring:
      application:
        version: ${APPLICATION_VERSION:1.0.0-SNAPSHOT}
      events:
        kafka-publisher:
          enabled: ${EVENTS_KAFKA_PUBLISHER_ENABLED:false}
      tracing:
        enabled: ${TRACING_ENABLED:false}
      logging:
        request-response:
          enabled: ${LOGGING_REQUEST_RESPONSE_ENABLED:true}
          exclude-paths: ${LOGGING_REQUEST_RESPONSE_EXCLUDES:}

# Foundation service configuration
foundation:
  service:
    url: ${FOUNDATION_SERVICE_URL:http://localhost:7080}

logging:
  level:
    root: INFO
    "[io.finhq]": ${LOGGING_LEVEL:DEBUG}
    "[org.springframework]": INFO
