   248→func (s *Service) getActiveProductList(ctx context.Context, productID string) ([]*bookkeepingpb.Product, error) {
   249→	key := productID
   250→	v, ok := s.cache.Get(key)
   251→	if ok {
   252→		return v.([]*bookkeepingpb.Product), nil
   253→	}
   254→	s.cachemu.Lock()
   255→	defer s.cachemu.Unlock()
   256→	v, ok = s.cache.Get(key)
   257→	if ok {
   258→		return v.([]*bookkeepingpb.Product), nil
   259→	}
   260→	productList, err := s.productService.ListProducts(
   261→		ctx, &bookkeepingpb.ListProductsRequest{
   262→			Context:   commons.NewBankstackRequestContext(),
   263→			ProductId: productID,
   264→		},
   265→	)
   266→	if err != nil {
   267→		return nil, err
   268→	}
   269→	var activeProductList []*bookkeepingpb.Product
   270→	for _, product := range productList.GetProducts() {
   271→		status := product.GetStatus().String()
   272→		if status == commons.ProductStatusActive {
   273→			activeProductList = append(activeProductList, product)
   274→		}
   275→	}
   276→	s.cache.Set(&tinylfu.Item{Key: key, Value: activeProductList, ExpireAt: time.Now().Add(24 * time.Hour)})
   277→	return activeProductList, nil
   278→}
