   376→func (s *Service) PrepareSettlementTransactions(ctx context.Context, transactions []*Transaction, fromTimestamp *timestamp.Timestamp, toTimestamp *timestamp.Timestamp, productID string, tenantID string) ([]*settlementpb.Transaction, error) {
   377→	ctx = commons.NewBankstackOutgoingContext(ctx, tenantID)
   378→
   379→	// Extract all transaction IDs for bulk call
   380→	transactionIDs := lo.Map(transactions, func(transaction *Transaction, _ int) string {
   381→		return transaction.TransactionID
   382→	})
   383→
   384→	// Bulk call to get all ledger entries grouped by transaction ID
   385→	ledgerResponse, err := s.dbkGeneralLedgerService.ListLedgerEntriesBySourceTransactionIds(
   386→		ctx, &bookkeepingpb.ListLedgerEntriesBySourceTransactionIdsRequest{
   387→			Context:              commons.NewBankstackRequestContext(),
   388→			ProductId:            productID,
   389→			SourceTransactionIds: transactionIDs,
   390→			FromTimestamp:        fromTimestamp,
   391→			ToTimestamp:          toTimestamp,
   392→			Timezone:             common.TimeZoneUTC,
   393→		},
   394→	)
   395→	if err != nil {
   396→		return nil, err
   397→	}
   398→
   399→	// Get the map of transaction ID to ledger entries
   400→	ledgerEntriesMap := ledgerResponse.GetLedgerEntriesByTransaction()
   401→
   402→	// Process each transaction with its corresponding ledger entries
   403→	var txns []*settlementpb.Transaction
   404→	for _, transaction := range transactions {
   405→		ledgerEntries := ledgerEntriesMap[transaction.TransactionID]
   406→
   407→		if len(ledgerEntries.GetLedgerEntries()) == 0 {
   408→			continue
   409→		}
   410→
   411→		txn, err := s.fetchSettlementTransactionsViaBookkeeping(ctx, transaction, ledgerEntries.GetLedgerEntries(), productID)
   412→		if err != nil {
   413→			continue
   414→		}
   415→		txns = append(txns, txn)
   416→	}
   417→
   418→	return txns, nil
   419→}
