   421→func createPartner(ledgerEntries []*bookkeepingpb.GeneralLedgerEntry, natureOfSource bookkeepingpb.AmountNature) *settlementpb.DBKPartners {
   422→	// Create Partner
   423→	partner := &settlementpb.DBKPartners{}
   424→	for _, entry := range ledgerEntries {
   425→		nature := entry.GetAmountNature()
   426→		tempStruct := &commonspb.Partner{}
   427→		accountType := ""
   428→		if nature == natureOfSource {
   429→			tempStruct.Type, tempStruct.Id, accountType = account.SplitDBKAccountNumber(entry.GetGlAccountNo())
   430→			partner.Source = tempStruct
   431→			partner.SourceAccountType = accountType
   432→		} else {
   433→			tempStruct.Type, tempStruct.Id, accountType = account.SplitDBKAccountNumber(entry.GetGlAccountNo())
   434→			partner.Destination = tempStruct
   435→			partner.DestinationAccountType = accountType
   436→		}
   437→	}
   438→	return partner
   439→}
