   441→func (s *Service) RevertTransaction(ctx context.Context, tenantID string, productID string, req *settlementpb.RevertTransactionRequest) (*settlementpb.RevertTransactionResponse, error) {
   442→	// Initialize timestamps first
   443→	req.FromTimestamp, req.ToTimestamp = commons.InitializeTimestamps(req.FromTimestamp, req.ToTimestamp)
   444→	logger.InfoWithFields(
   445→		ctx, "RevertTransaction", map[string]interface{}{
   446→			"transaction_id": req.TransactionId,
   447→			"metadata":       req.Metadata,
   448→		},
   449→	)
   450→	// Validate the request
   451→	if err := ValidateRevertTransactionRequest(req); err != nil {
   452→		return nil, pkg_errors.InvalidArgument.WithMessage(err.Error())
   453→	}
   454→
   455→	// Check if the transaction is already reverted
   456→	if IsReversalTransactionID(req.TransactionId) {
   457→		return nil, pkg_errors.FailedPrecondition.WithMessage("cannot revert an already reverted transaction")
   458→	}
   459→
   460→	// Check if the reverted transaction ID already exists
   461→	potentialRevertedTxnID := req.TransactionId + commons.RevertTransactionIdentifierSuffix
   462→	exists, err := s.repo.transactionExists(ctx, tenantID, potentialRevertedTxnID, req.FromTimestamp, req.ToTimestamp)
   463→	if err != nil {
   464→		return nil, err
   465→	}
   466→	if exists {
   467→		return nil, pkg_errors.AlreadyExists.WithMessage("reverted transaction ID already exists")
   468→	}
   469→	// Retrieve the original transaction
   470→	originalTxn, err := s.GetTransactionById(
   471→		ctx, tenantID, productID, &settlementpb.GetTransactionByIdRequest{
   472→			TransactionId: req.TransactionId,
   473→			FromTimestamp: req.FromTimestamp,
   474→			ToTimestamp:   req.ToTimestamp,
   475→		},
   476→	)
   477→	if err != nil {
   478→		return nil, err
   479→	}
   480→
   481→	// Check if the reversed transaction type exists in the transaction_types table
   482→	reversedTransactionType := GetReversalTransactionType(originalTxn.Transaction.Type)
   483→	_, err = s.transactionTypeService.GetTransactionTypeByName(
   484→		ctx, tenantID, &settlementpb.GetTransactionTypeByNameRequest{
   485→			Context:             &bankstack_commons.RequestContext{},
   486→			TransactionTypeName: reversedTransactionType,
   487→		},
   488→	)
   489→	if err != nil {
   490→		return nil, pkg_errors.FailedPrecondition.WithMessage("System not ready for this operation → System's fault/ onboarding issue")
   491→	}
   492→	// Create the reversal transaction
   493→	reversalTxn := createReversalTransaction(originalTxn.Transaction, req.Metadata)
   494→	// Post the reversal transaction
   495→	revertedTxn, err := s.PostTransactionV2(
   496→		ctx, tenantID, productID, &settlementpb.PostTransactionV2Request{
   497→			Context:     commons.NewBankstackRequestContext(),
   498→			Transaction: reversalTxn,
   499→		})
   500→	if err != nil {
   501→		return nil, err
   502→	}
   503→	//update the current transaction to include reversal flag
   504→	metadata := map[string]interface{}{commons.FinhqRevertTransactionMetadataPrefix: true}
   505→	metadatapb, err := structpb.NewStruct(metadata)
   506→	_, err = s.UpdateTransactionMetadata(
   507→		ctx, tenantID, &settlementpb.UpdateTransactionMetadataRequest{
   508→			Context:       commons.NewBankstackRequestContext(),
   509→			Metadata:      metadatapb,
   510→			TransactionId: req.TransactionId,
   511→		})
   512→	if err != nil {
   513→		return nil, err
   514→	}
   515→	// Return the response
   516→	return &settlementpb.RevertTransactionResponse{
   517→		Status: &bankstack_commons.ResponseStatus{
   518→			Success: true,
   519→		},
   520→		RevertedTransaction: revertedTxn.Transaction,
   521→	}, nil
   522→}
