   524→// createReversalTransaction creates a new transaction that reverses the original transaction
   525→func createReversalTransaction(originalTxn *settlementpb.Transaction, requestMetadata *structpb.Struct) *settlementpb.Transaction {
   526→	// Create a copy of the transaction
