package in.dreamplug.darwin;

import static in.dreamplug.darwin.configuration.BeanConstants.ENTITY_PATH;

import java.util.TimeZone;
import javax.annotation.PostConstruct;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.boot.autoconfigure.domain.EntityScan;
import org.springframework.context.annotation.ComponentScan;
import org.springframework.context.annotation.EnableAspectJAutoProxy;
import org.springframework.retry.annotation.EnableRetry;
import org.springframework.scheduling.annotation.EnableScheduling;

@EnableRetry
@SpringBootApplication
@EntityScan(basePackages = ENTITY_PATH)
@EnableAspectJAutoProxy
@EnableScheduling
@ComponentScan(
        basePackages = {"in.dreamplug.darwin", "in.dreamplug.commons.springboot", "in.dreamplug.payments.domain"})
public class DarwinApplication {

    public static void main(String[] args) {
        SpringApplication.run(DarwinApplication.class);
    }

    @PostConstruct
    void setTimezone() {
        TimeZone.setDefault(TimeZone.getTimeZone("UTC"));
    }
}

