package in.dreamplug.darwin.entity.commons;

import in.dreamplug.darwin.commons.dto.ApplicationContext;
import in.dreamplug.darwin.util.Constant;
import java.time.LocalDateTime;
import java.util.Objects;
import javax.persistence.*;
import lombok.Getter;
import lombok.Setter;
import org.hibernate.annotations.CreationTimestamp;
import org.hibernate.annotations.UpdateTimestamp;

@Getter
@Setter
@MappedSuperclass
public class BaseEntity {
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    @CreationTimestamp
    @Column(name = "created_at")
    private LocalDateTime createAt = LocalDateTime.now();

    @UpdateTimestamp
    @Column(name = "updated_at")
    private LocalDateTime updatedAt = LocalDateTime.now();

    @Column(name = "created_by")
    private String createdBy;

    @Column(name = "updated_by")
    private String updatedBy;

    @PrePersist
    public void onCreate() {
        String clientName = Constant.DEFAULT_CLIENT_NAME;

        if (Objects.nonNull(ApplicationContext.instance().getContext().getClient())) {
            clientName = ApplicationContext.instance().getContext().getClient().getName();
        }

        this.setCreatedBy(clientName);
        this.setUpdatedBy(clientName);
    }

    @PreUpdate
    public void onUpdate() {
        String clientName = Constant.DEFAULT_CLIENT_NAME;

        if (Objects.nonNull(ApplicationContext.instance().getContext().getClient())) {
            clientName = ApplicationContext.instance().getContext().getClient().getName();
        }

        this.setUpdatedBy(clientName);
    }
}

