package in.dreamplug.darwin.configuration.database;

import static in.dreamplug.darwin.configuration.BeanConstants.*;

import com.zaxxer.hikari.HikariConfig;
import com.zaxxer.hikari.HikariDataSource;
import in.dreamplug.commons.service.maintenance.handler.HikariMaintenanceHandler;
import in.dreamplug.commons.service.maintenance.handler.MaintenanceManager;
import in.dreamplug.darwin.configuration.database.annotation.ReplicaRepository;
import java.net.URISyntaxException;
import java.util.Properties;
import javax.persistence.EntityManagerFactory;
import javax.sql.DataSource;
import lombok.extern.slf4j.Slf4j;
import org.hibernate.cfg.AvailableSettings;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.beans.factory.config.ConfigurableListableBeanFactory;
import org.springframework.boot.orm.jpa.EntityManagerFactoryBuilder;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.ComponentScan;
import org.springframework.context.annotation.Configuration;
import org.springframework.data.jpa.repository.config.EnableJpaRepositories;
import org.springframework.orm.hibernate5.SpringBeanContainer;
import org.springframework.orm.jpa.JpaTransactionManager;
import org.springframework.orm.jpa.JpaVendorAdapter;
import org.springframework.orm.jpa.LocalContainerEntityManagerFactoryBean;
import org.springframework.orm.jpa.vendor.HibernateJpaVendorAdapter;
import org.springframework.transaction.PlatformTransactionManager;

@Configuration
@EnableJpaRepositories(
        basePackages = {REPO_PATH, DAO_PATH},
        includeFilters = @ComponentScan.Filter(ReplicaRepository.class),
        entityManagerFactoryRef = REPLICA_ENTITY_MANAGER_FACTORY,
        transactionManagerRef = REPLICA_TRANSACTION_MANAGER)
@Slf4j
public class ReplicaDataSourceConfiguration {
    @Autowired
    @Qualifier("defaultHibernateProperties")
    Properties defaultHibernateProperties;

    @Value("${spring.datasource.hikari.idleTimeout}")
    private Long hikariIdleTimeout;

    @Value("${spring.datasource.hikari.connectionTimeout}")
    private Long hikariConnectionTimeout;

    @Value("${replica.minimumIdle}")
    private Long replicaMinimumIdle;

    @Value("${replica.maximumPoolSize}")
    private Long replicaMaximumPoolSize;

    @Value("${spring.datasource.hikari.max-lifetime}")
    private Long maxLifeTime;

    @Value("${spring.datasource.hikari.leak-detection-threshold}")
    private Long leakDetectionThreshold;

    @Value("${replica.datasource.driverClassName}")
    private String driverClassName;

    @Value("${replica.datasource.url}")
    private String url;

    @Value("${replica.datasource.username}")
    private String username;

    @Value("${replica.datasource.password}")
    private String password;

    @Value("${replica.transactionTimeout}")
    private Long replicaTransactionTimeout;

    @Bean(name = REPLICA_DATA_SOURCE)
    public DataSource dataSource(MaintenanceManager maintenanceManager) {

        HikariConfig hikariConfig = new HikariConfig();
        hikariConfig.setIdleTimeout(hikariIdleTimeout);
        hikariConfig.setConnectionTimeout(hikariConnectionTimeout);
        hikariConfig.setMinimumIdle(replicaMinimumIdle.intValue());
        hikariConfig.setMaximumPoolSize(replicaMaximumPoolSize.intValue());
        hikariConfig.setReadOnly(true);
        hikariConfig.setMaxLifetime(maxLifeTime);
        hikariConfig.setPoolName("hikari_replica");
        hikariConfig.setLeakDetectionThreshold(leakDetectionThreshold);
        hikariConfig.setReadOnly(true);
        hikariConfig.setJdbcUrl(url);
        hikariConfig.setUsername(username);
        hikariConfig.setPassword(password);
        hikariConfig.setDriverClassName(driverClassName);

        HikariDataSource replicaDataSource = new HikariDataSource(hikariConfig);
        maintenanceManager.registerHandler(new HikariMaintenanceHandler(replicaDataSource));

        return replicaDataSource;
    }

    @Bean(name = REPLICA_ENTITY_MANAGER_FACTORY)
    public LocalContainerEntityManagerFactoryBean entityManagerFactory(
            EntityManagerFactoryBuilder builder,
            @Qualifier(REPLICA_DATA_SOURCE) DataSource dataSource,
            ConfigurableListableBeanFactory beanFactory) {
        LocalContainerEntityManagerFactoryBean localContainerEntityManagerFactoryBean = builder.dataSource(dataSource)
                .packages(ENTITY_PATH)
                .persistenceUnit(REPLICA_PERSISTENCE_UNIT)
                .build();

        JpaVendorAdapter vendorAdapter = new HibernateJpaVendorAdapter();
        localContainerEntityManagerFactoryBean.setJpaVendorAdapter(vendorAdapter);
        localContainerEntityManagerFactoryBean.setJpaProperties(defaultHibernateProperties);
        localContainerEntityManagerFactoryBean
                .getJpaPropertyMap()
                .put(AvailableSettings.BEAN_CONTAINER, new SpringBeanContainer(beanFactory));

        return localContainerEntityManagerFactoryBean;
    }

    @Bean(name = REPLICA_TRANSACTION_MANAGER)
    public PlatformTransactionManager primaryTransactionManager(
            @Qualifier(REPLICA_ENTITY_MANAGER_FACTORY) EntityManagerFactory entityManagerFactory)
            throws URISyntaxException {
        JpaTransactionManager transactionManager = new JpaTransactionManager();
        transactionManager.setEntityManagerFactory(entityManagerFactory);
        transactionManager.setDefaultTimeout(replicaTransactionTimeout.intValue());
        return transactionManager;
    }
}

