package in.dreamplug.darwin.cache;

import com.github.benmanes.caffeine.cache.AsyncLoadingCache;
import in.dreamplug.commons.springboot.cache.AbstractDPManagedCache;
import in.dreamplug.commons.springboot.cache.DPCache;
import in.dreamplug.commons.springboot.cache.IDPManagedCache;
import in.dreamplug.commons.springboot.cache.util.DPCacheBuilder;
import in.dreamplug.darwin.client.merchant.MerchantServiceClientImpl;
import in.dreamplug.darwin.configuration.CacheConfig;
import in.dreamplug.darwin.util.Constant;
import in.dreamplug.payments.domain.commons.MerchantSummary;
import java.util.Optional;
import java.util.concurrent.TimeUnit;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.apache.commons.lang3.BooleanUtils;
import org.apache.commons.lang3.StringUtils;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

@Slf4j
@Component
@RequiredArgsConstructor(onConstructor_ = {@Autowired})
public class MerchantCache extends AbstractDPManagedCache implements IDPManagedCache {
    private final MerchantServiceClientImpl merchantServiceClientImpl;

    private final CacheConfig cacheConfig;

    @DPCache(cacheName = Constant.CacheName.MERCHANT_ENTITY_CACHE)
    private AsyncLoadingCache<String, MerchantSummary> cache;

    @DPCache(cacheName = Constant.CacheName.MERCHANT_PARTNER_ENTITY_CACHE)
    private AsyncLoadingCache<String, Boolean> merchantPartnerAssociationCache;

    private static final Integer TIMEOUT_IN_MS = 100;

    @Override
    public void buildCache() {
        cache = DPCacheBuilder.getBuilder()
                .refreshAfterWrite(cacheConfig.getMerchantCacheTTL(), cacheConfig.getMerchantCacheTTLType())
                .buildAsync(key ->
                        merchantServiceClientImpl.fetchMerchantSummaryById(key).orElse(null));

        merchantPartnerAssociationCache = DPCacheBuilder.getBuilder()
                .refreshAfterWrite(cacheConfig.getMerchantCacheTTL(), cacheConfig.getMerchantCacheTTLType())
                .buildAsync(this::isPartnerAuthorizedForMerchant);
    }

    public Optional<MerchantSummary> findMerchantSummaryByIdOnlyActive(String merchantId) {
        if (StringUtils.isBlank(merchantId)) {
            return Optional.empty();
        }

        try {
            return Optional.ofNullable(cache.get(merchantId).get(TIMEOUT_IN_MS, TimeUnit.MILLISECONDS));
        } catch (Exception e) {
            log.debug("Error while calling fetching merchant summary for merchant id {}", merchantId);
            return Optional.empty();
        }
    }

    public boolean isPartnerAuthorizedForMerchant(String partnerId, String merchantId) {
        if (StringUtils.isBlank(partnerId) || StringUtils.isBlank(merchantId)) {
            return false;
        }

        String cacheKey = getPartnerMerchantCacheKey(partnerId, merchantId);

        try {
            return BooleanUtils.isTrue(
                    merchantPartnerAssociationCache.get(cacheKey).get(TIMEOUT_IN_MS, TimeUnit.MILLISECONDS));
        } catch (Exception e) {
            log.debug("Error while calling fetching merchant summary for merchant id {}", merchantId);
            return false;
        }
    }

    public boolean isPartnerAuthorizedForMerchant(String cacheKey) {
        String[] cacheKeySplit = cacheKey.split("#");
        return merchantServiceClientImpl.isPartnerAuthorizedForMerchant(cacheKeySplit[0], cacheKeySplit[1]);
    }

    private String getPartnerMerchantCacheKey(String partnerId, String merchantId) {
        return partnerId + "#" + merchantId;
    }
}

