package in.dreamplug.darwin.cache;

import com.github.benmanes.caffeine.cache.LoadingCache;
import in.dreamplug.commons.springboot.cache.AbstractDPManagedCache;
import in.dreamplug.commons.springboot.cache.DPCache;
import in.dreamplug.commons.springboot.cache.IDPManagedCache;
import in.dreamplug.commons.springboot.cache.util.DPCacheBuilder;
import in.dreamplug.darwin.configuration.CacheConfig;
import in.dreamplug.darwin.entity.ClientEntity;
import in.dreamplug.darwin.entity.ClientSettingsEntity;
import in.dreamplug.darwin.error.Error;
import in.dreamplug.darwin.repository.ClientRepository;
import in.dreamplug.darwin.util.Constant;
import in.dreamplug.payments.enums.ClientSettingKey;
import java.util.HashMap;
import java.util.Map;
import java.util.Objects;
import java.util.Optional;
import java.util.stream.Collectors;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

@Slf4j
@Component
@RequiredArgsConstructor(onConstructor_ = {@Autowired})
public class ClientConfigurationCache extends AbstractDPManagedCache implements IDPManagedCache {
    private final CacheConfig cacheConfig;

    private final ClientRepository clientRepository;

    @DPCache(cacheName = Constant.CacheName.CLIENT_CONFIGURATION_CACHE)
    private LoadingCache<Long, Map<ClientSettingKey, String>> idCache;

    @DPCache(cacheName = Constant.CacheName.CLIENT_BY_ID_CACHE)
    private LoadingCache<Long, ClientEntity> clientByIdCache;

    @DPCache(cacheName = Constant.CacheName.CLIENT_BY_EXTERNAL_ID_CACHE)
    private LoadingCache<String, ClientEntity> clientByExternalIdCache;

    @DPCache(cacheName = Constant.CacheName.CLIENT_BY_MERCHANT_ID_CACHE)
    private LoadingCache<String, ClientEntity> clientByMerchantId;

    @Override
    public void buildCache() {
        idCache = DPCacheBuilder.getBuilder()
                .refreshAfterWrite(cacheConfig.getMasterConfigurationTTL(), cacheConfig.getMasterConfigurationTTLType())
                .build(this::loadClientSettings);

        clientByIdCache = DPCacheBuilder.getBuilder()
                .refreshAfterWrite(cacheConfig.getClientCacheByIdConfig(), cacheConfig.getClientCacheByIdTTLType())
                .build(this::loadClientEntityById);

        clientByExternalIdCache = DPCacheBuilder.getBuilder()
                .refreshAfterWrite(
                        cacheConfig.getClientCacheByExternalIdConfig(), cacheConfig.getClientCacheByExternalIdTTLType())
                .build(this::loadClientEntityByExternalId);

        clientByMerchantId = DPCacheBuilder.getBuilder()
                .refreshAfterWrite(
                        cacheConfig.getClientCacheByExternalIdConfig(), cacheConfig.getClientCacheByExternalIdTTLType())
                .build(this::loadClientEntityByMerchantId);
    }

    public Map<ClientSettingKey, String> loadClientSettings(Long clientId) {
        ClientEntity clientEntity = clientRepository
                .findById(clientId)
                .orElseThrow(() -> Error.invalid_client_id.getBuilder().build());

        Map<ClientSettingKey, String> settings = clientEntity.getSettings().stream()
                .collect(Collectors.toMap(ClientSettingsEntity::getSettingKey, ClientSettingsEntity::getSettingValue));

        return settings;
    }

    public ClientEntity loadClientEntityById(Long clientId) {
        return clientRepository
                .findById(clientId)
                .orElseThrow(() -> Error.invalid_client_id.getBuilder().build());
    }

    public ClientEntity loadClientEntityByExternalId(String clientId) {
        return clientRepository
                .findByExternalId(clientId)
                .orElseThrow(() -> Error.invalid_client_id.getBuilder().build());
    }

    public ClientEntity loadClientEntityByMerchantId(String merchantId) {
        return clientRepository
                .findByMerchantId(merchantId)
                .orElseThrow(() -> Error.invalid_merchant_id.getBuilder().build());
    }

    public Map<ClientSettingKey, String> getClientSettings(Long clientId) {
        try {
            return Objects.isNull(idCache.get(clientId)) ? new HashMap<>() : idCache.get(clientId);
        } catch (Exception e) {
            log.error("Error while fetching from cache {}", clientId);
            return new HashMap<>();
        }
    }

    public Optional<String> getClientSettingByKey(Long clientId, ClientSettingKey settingKey) {
        try {
            return Optional.ofNullable(idCache.get(clientId)).map(settingMap -> settingMap.get(settingKey));
        } catch (Exception e) {
            log.error("Error while fetching from cache {}", clientId);
            return Optional.empty();
        }
    }

    public ClientEntity getClientById(Long clientId) {
        try {
            return clientByIdCache.get(clientId);
        } catch (Exception e) {
            log.error("getClientById : Error while fetching from cache {}", clientId);
            return null;
        }
    }

    public ClientEntity getClientByExternalId(String clientId) {
        try {
            return clientByExternalIdCache.get(clientId);
        } catch (Exception e) {
            log.error("getClientByExternalId: Error while fetching from cache {}", clientId);
            return null;
        }
    }

    public ClientEntity getClientByMerchantId(String merchantId) {
        try {
            return clientByMerchantId.get(merchantId);
        } catch (Exception e) {
            log.error("getClientByMerchantId: Error while fetching from cache {}", merchantId);
            return null;
        }
    }

    public Optional<ClientEntity> getActiveClientByExternalId(String clientId) {
        ClientEntity clientEntity = getClientByExternalId(clientId);
        return isClientActive(clientEntity) ? Optional.of(clientEntity) : Optional.empty();
    }

    public Optional<ClientEntity> getActiveClientByMerchantId(String merchantId) {
        ClientEntity clientEntity = getClientByMerchantId(merchantId);
        return isClientActive(clientEntity) ? Optional.of(clientEntity) : Optional.empty();
    }

    private boolean isClientActive(ClientEntity clientEntity) {
        return Objects.nonNull(clientEntity) && Boolean.FALSE.equals(clientEntity.getDeleted());
    }
}

