     2→
     3→import in.dreamplug.darwin.commons.dto.ApplicationContext;
     4→import in.dreamplug.darwin.entity.ClientEntity;
     5→import in.dreamplug.darwin.error.Error;
     6→import in.dreamplug.darwin.service.client.ClientService;
     7→import in.dreamplug.darwin.util.Constant;
     8→import java.util.Objects;
     9→import javax.servlet.http.HttpServletRequest;
    10→import javax.servlet.http.HttpServletResponse;
    11→import lombok.RequiredArgsConstructor;
    12→import lombok.extern.slf4j.Slf4j;
    13→import org.apache.commons.lang3.StringUtils;
    14→import org.springframework.beans.factory.annotation.Autowired;
    15→import org.springframework.stereotype.Component;
    16→
    17→@Slf4j
    18→@Component
    19→@RequiredArgsConstructor(onConstructor_ = {@Autowired})
    20→public class ApplicationContextBuilderFilter extends SkipContextBuildAwareFilter {
    21→    private final ClientService clientService;
    22→
    23→    @Override
    24→    public boolean preHandleOverride(HttpServletRequest request, HttpServletResponse response, Object handler) {
    25→        validateAndEnrichClientContextFromRequest(request);
    26→
    27→        enrichAuxiliaryContextFromRequest(request);
    28→
    29→        return true;
    30→    }
    31→
    32→    private void enrichAuxiliaryContextFromRequest(HttpServletRequest request) {
    33→        String deviceId = request.getHeader(Constant.DEVICE_ID_HEADER_KEY);
    34→        String os = request.getHeader(Constant.OS_HEADER);
    35→        String osVersion = request.getHeader(Constant.OS_VERSION_HEADER);
    36→        String appVersion = request.getHeader(Constant.APP_VERSION_HEADER);
    37→
    38→        ApplicationContext.instance().getContext().setDeviceId(deviceId);
    39→        ApplicationContext.instance().getContext().setOs(os);
    40→        ApplicationContext.instance().getContext().setOsVersion(osVersion);
    41→        ApplicationContext.instance().getContext().setAppVersion(appVersion);
    42→    }
    43→
    44→    private void validateAndEnrichClientContextFromRequest(HttpServletRequest request) {
    45→        String clientId = request.getHeader(Constant.CLIENT_HEADER_KEY);
    46→        String merchantId = request.getHeader(Constant.MERCHANT_HEADER_KEY);
    47→
    48→        if (StringUtils.isEmpty(merchantId)) {
    49→            log.debug("merchant id missing in header");
    50→            merchantId = ApplicationContext.instance().getContext().getMerchantId();
    51→        }
    52→
    53→        if (StringUtils.isEmpty(clientId) && StringUtils.isEmpty(merchantId)) {
    54→            log.error("Client Id or Merchant Id details missing in the header");
    55→            throw Error.authorization_error
    56→                    .getBuilder()
    57→                    .message("Authentication failed")
    58→                    .build();
    59→        }
    60→
    61→        ClientEntity clientEntity = clientService.getClientEntity(clientId, merchantId);
    62→
    63→        if (Objects.nonNull(clientEntity) && StringUtils.isEmpty(clientEntity.getMerchantId())) {
    64→            log.error("Merchant id is missing in the client entity for client id {}", clientEntity.getExternalId());
    65→        }
    66→
    67→        ApplicationContext.instance().getContext().setClient(clientEntity);
    68→    }
    69→
    70→    @Override
    71→    public void afterCompletion(
    72→            HttpServletRequest request, HttpServletResponse response, Object handler, Exception ex) {
    73→        ApplicationContext.instance().clear();
    74→    }
    75→}
