import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

@Slf4j
@Component
@RequiredArgsConstructor(onConstructor_ = {@Autowired})
public class ApplicationContextBuilderFilter extends SkipContextBuildAwareFilter {
    private final ClientService clientService;

    @Override
    public boolean preHandleOverride(HttpServletRequest request, HttpServletResponse response, Object handler) {
        validateAndEnrichClientContextFromRequest(request);

        enrichAuxiliaryContextFromRequest(request);

        return true;
    }

    private void enrichAuxiliaryContextFromRequest(HttpServletRequest request) {
        String deviceId = request.getHeader(Constant.DEVICE_ID_HEADER_KEY);
        String os = request.getHeader(Constant.OS_HEADER);
        String osVersion = request.getHeader(Constant.OS_VERSION_HEADER);
        String appVersion = request.getHeader(Constant.APP_VERSION_HEADER);

        ApplicationContext.instance().getContext().setDeviceId(deviceId);
        ApplicationContext.instance().getContext().setOs(os);
        ApplicationContext.instance().getContext().setOsVersion(osVersion);
        ApplicationContext.instance().getContext().setAppVersion(appVersion);
    }

    private void validateAndEnrichClientContextFromRequest(HttpServletRequest request) {
        String clientId = request.getHeader(Constant.CLIENT_HEADER_KEY);
        String merchantId = request.getHeader(Constant.MERCHANT_HEADER_KEY);

        if (StringUtils.isEmpty(merchantId)) {
            log.debug("merchant id missing in header");
            merchantId = ApplicationContext.instance().getContext().getMerchantId();
        }

        if (StringUtils.isEmpty(clientId) && StringUtils.isEmpty(merchantId)) {
            log.error("Client Id or Merchant Id details missing in the header");
            throw Error.authorization_error
                    .getBuilder()
                    .message("Authentication failed")
                    .build();
        }

        ClientEntity clientEntity = clientService.getClientEntity(clientId, merchantId);

        if (Objects.nonNull(clientEntity) && StringUtils.isEmpty(clientEntity.getMerchantId())) {
            log.error("Merchant id is missing in the client entity for client id {}", clientEntity.getExternalId());
        }

        ApplicationContext.instance().getContext().setClient(clientEntity);
    }

    @Override
    public void afterCompletion(
            HttpServletRequest request, HttpServletResponse response, Object handler, Exception ex) {
        ApplicationContext.instance().clear();
    }
}

