package in.dreamplug.darwin.commons.spring.filters;

import static in.dreamplug.darwin.util.Constant.MERCHANT_HEADER_KEY;

import in.dreamplug.darwin.client.syndicate.SyndicateClient;
import in.dreamplug.darwin.client.syndicate.dto.BasicAuthRequest;
import in.dreamplug.darwin.client.syndicate.dto.BasicAuthResponse;
import in.dreamplug.darwin.commons.dto.ApplicationContext;
import in.dreamplug.darwin.error.Error;
import in.dreamplug.darwin.util.Constant;
import java.util.HashMap;
import java.util.Map;
import java.util.Optional;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.apache.commons.lang3.StringUtils;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

@Slf4j
@Component
@RequiredArgsConstructor(onConstructor_ = {@Autowired})
public class PAMerchantAuthenticationFilter extends SkipContextBuildAwareFilter {

    private final SyndicateClient syndicateClient;

    @Override
    public boolean preHandleOverride(HttpServletRequest request, HttpServletResponse response, Object handler) {

        final String paMerchantToken = request.getHeader(Constant.PA_MERCHANT_ACCESS_TOKEN);

        if (StringUtils.isEmpty(paMerchantToken)) {
            return true;
        }

        Map<String, String> headers = validateAndGetPAHeaders(request, paMerchantToken);
        ApplicationContext.instance().getContext().setMerchantId(headers.get(MERCHANT_HEADER_KEY));

        return true;
    }

    private Map<String, String> validateAndGetPAHeaders(HttpServletRequest httpServletRequest, String paMerchantToken) {

        BasicAuthRequest request = BasicAuthRequest.builder()
                .credential(paMerchantToken)
                .method(httpServletRequest.getMethod())
                .url(httpServletRequest.getRequestURI())
                .build();

        Optional<BasicAuthResponse> authResponse = syndicateClient.authenticateClient(request);
        if (authResponse.isPresent()) {
            return Optional.ofNullable(authResponse.get().getHeaders()).orElse(new HashMap<>());
        } else {
            throw Error.authorization_error
                    .getBuilder()
                    .message("Authorization token not valid")
                    .build();
        }
    }

    @Override
    public void afterCompletion(
            HttpServletRequest request, HttpServletResponse response, Object handler, Exception ex) {
        ApplicationContext.instance().clear();
    }
}

