package in.dreamplug.darwin.configuration;

import lombok.extern.slf4j.Slf4j;
import org.redisson.Redisson;
import org.redisson.api.RedissonClient;
import org.redisson.codec.Kryo5Codec;
import org.redisson.config.Config;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;

@Configuration
@Slf4j
public class RedissonAutoConfiguration {
    @Value("${aws.elastiCache.url}")
    private String url;

    @Value("${aws.elastiCache.port}")
    private String port;

    @Value("${aws.elastiCache.authEnabled}")
    private Boolean authEnabled;

    @Value("${aws.elastiCache.password}")
    private String password;

    @Value("${aws.elastiCache.watchDogTimeout}")
    private Long watchDogTimeout;

    @Bean(destroyMethod = "shutdown")
    public RedissonClient redissonClient() {
        Config config = new Config();
        String schema = authEnabled ? "rediss://" : "redis://";
        String redisUrl = schema + url + ":" + port;
        config.useSingleServer().setAddress(redisUrl).setPassword(password.compareTo("dummy") == 0 ? null : password);
        config.setLockWatchdogTimeout(watchDogTimeout);
        config.setCodec(new Kryo5Codec());
        return Redisson.create(config);
    }
}

