    12→@RequiredArgsConstructor
    13→public abstract class AbstractDynamoDBDAO<T> {
    14→    private final Class<T> tClass;
    15→
    16→    private final DynamoDBMapper dynamoDBMapper;
    17→
    18→    public void save(T entity) {
    19→        dynamoDBMapper.save(entity);
    20→    }
    21→
    22→    public void saveIfNotExists(T entity, List<String> uniqueKeys) {
    23→        if (CollectionUtils.isEmpty(uniqueKeys)) {
    24→            save(entity);
    25→        }
    26→        DynamoDBSaveExpression saveExpression = new DynamoDBSaveExpression();
    27→        Map<String, ExpectedAttributeValue> expected = new HashMap<>();
    28→        uniqueKeys.forEach(key -> {
    29→            expected.put(key, new ExpectedAttributeValue().withExists(false));
    30→        });
    31→        saveExpression.setExpected(expected);
    32→        dynamoDBMapper.save(entity, saveExpression);
    33→    }
    34→
    35→    public T findByHashKey(Object hashKey) {
    36→        return dynamoDBMapper.load(tClass, hashKey);
    37→    }
    38→}
