buildscript {
    repositories {
        mavenLocal()
        jcenter()
        mavenCentral()
    }
    dependencies {
        classpath 'org.springframework.boot:spring-boot-gradle-plugin:2.2.6.RELEASE'
    }
}

plugins {
    id 'org.springframework.boot' version '2.7.14'
    id 'io.spring.dependency-management' version '1.0.15.RELEASE'
    id 'java'
    id "idea"
    id "org.sonarqube" version "3.5.0.2730"
    id "jacoco"
    id 'com.diffplug.spotless' version '6.23.3'
}

jacoco {
    toolVersion = '0.8.8'
}

group 'in.dreamplug.payment'
version '1.0.0-SNAPSHOT'
sourceCompatibility = 17
targetCompatibility = 17

apply plugin: 'java'
apply plugin: 'idea'
apply plugin: 'org.springframework.boot'
apply plugin: 'io.spring.dependency-management'

bootJar {
    baseName = 'payment-service'
    version = '1.0.0'
}

configurations {
    all {
        exclude group: 'org.springframework.boot', module: 'spring-boot-starter-logging'
        resolutionStrategy {
            force 'io.grpc:grpc-core:1.45.0'
        }
    }
}

repositories {
    mavenLocal()
    mavenCentral()
    maven {
        url "https://jcenter.bintray.com"
    }
    maven {
        url "https://nexus.infra.dreamplug.net/repository/maven-public"
    }
    maven {
        url "https://nexus.infra.dreamplug.net/repository/maven-snapshots"
    }
    maven {
        url "https://nexus.infra.dreamplug.net/repository/protorepo-snapshots"
    }
}

ext {
    set('lombokVersion', "1.18.26")
}

dependencies {
    implementation ('in.dreamplug.payment:payment-domain:1.10.26') {
        exclude group: 'in.dreamplug.falcon', module : 'java-client'
        exclude group: 'in.dreamplug.falcon', module : 'models'
    }
    implementation 'club.cred.payments:fees:0.18.0'
    implementation(group: 'in.dreamplug.pricing', name: 'pricing-utils', version: '0.0.12.ad797fb6cd') {
        exclude(group: 'club.cred.payments.pricing')
        exclude(group: 'com.google.protobuf')
        exclude(group: 'io.grpc')
        exclude(group: 'com.google.api.grpc')
        exclude(group: 'in.dreamplug.grpc.commons.service')
        exclude(group: 'club.cred.bankstack.framework')
    }
    implementation 'org.springframework.boot:spring-boot-starter'
    implementation 'org.springframework.boot:spring-boot-starter'
    implementation 'org.springframework.boot:spring-boot-starter-web'
    implementation 'org.springframework.boot:spring-boot-starter-actuator'
    implementation 'org.springframework.boot:spring-boot-starter-aop'
    implementation group: 'org.springframework.retry', name: 'spring-retry', version: '1.3.0'
    implementation group: 'in.dreamplug', name: 'user-service-client', version: '4.0.1'
    implementation 'org.apache.httpcomponents.client5:httpclient5:5.2'

    implementation 'com.datadoghq:dd-trace-api:0.60.1'
    implementation("org.thymeleaf:thymeleaf:3.1.2.RELEASE")

    implementation 'in.dreamplug.merchantservice:merchant-service-client:1.0.3'
    implementation group: 'org.projectlombok', name: 'lombok', version: "${lombokVersion}"
    annotationProcessor "org.projectlombok:lombok:${lombokVersion}"
    testCompileOnly group: 'org.projectlombok', name: 'lombok', version: "${lombokVersion}"
    testAnnotationProcessor group: 'org.projectlombok', name: 'lombok', version: "${lombokVersion}"

    //start clients
    implementation('in.dreamplug.external:razorpay-payment-client:1.2.18') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:plutus-payment-client:1.0.2') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:razorpay-direct-client:1.2.19'){
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:pinelabs-provider-client:1.0.10') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:cred-wallet-client:1.0.6'){
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:cred-bnpl:1.0.3'){
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:web-miner-client:1.0.0'){
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation ( group: 'in.dreamplug.falcon', name: 'java-client', version: '1.30.0' ) {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation 'org.hashids:hashids:1.0.3'

    implementation('in.dreamplug.external:lending-provider-client:1.3.3') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.client:juspay-java-client:2.5.11') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:axis-upi-client:0.5.3') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:icici-upi-client:1.0.10') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:ybl-upi-client:1.0.10') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }
    implementation('in.dreamplug.external:rbl-upi-client:1.0.4') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:payu-provider-client:1.0.16') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:cybersource-provider-client:1.0.35') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:cred-ppi-client:1.0.2') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
    }

    implementation('in.dreamplug.external:lazypay-client:1.0.11') {
        exclude group: 'org.slf4j', module: 'slf4j-simple'
