package in.dreamplug.darwin.controller;

import in.dreamplug.darwin.commons.dto.SkipContextBuild;
import in.dreamplug.darwin.service.WebhookService;
import in.dreamplug.payments.domain.order.v2.Order;
import in.dreamplug.payments.dto.request.v2.WebhookRequest;
import javax.validation.Valid;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.web.bind.annotation.*;

@Slf4j
@RestController
@RequestMapping("/v1/webhooks")
@RequiredArgsConstructor(onConstructor_ = {@Autowired})
public class WebhookController {

    private final WebhookService webhookService;

    @SkipContextBuild
    @PostMapping
    public @ResponseBody Order processWebhook(@RequestBody @Valid WebhookRequest webhookRequest) {
        // todo change log level to debug
        log.info("processWebhook request : {}", webhookRequest);
        try {
            return webhookService.processWebhook(webhookRequest);
        } catch (Exception e) {
            log.error("Exception while calling processWebhook", e);
            throw e;
        }
    }
}
