* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

:root {
  --bg-primary: #1a1a2e;
  --bg-secondary: #16213e;
  --bg-tertiary: #0f0f23;
  --text-primary: #e4e4e7;
  --text-secondary: #a1a1aa;
  --accent: #6366f1;
  --accent-hover: #4f46e5;
  --border: #27272a;
  --error: #ef4444;
  --success: #22c55e;
}

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
  background: var(--bg-primary);
  color: var(--text-primary);
  height: 100vh;
  overflow: hidden;
}

.container {
  display: flex;
  flex-direction: column;
  height: 100vh;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px 20px;
  background: var(--bg-secondary);
  border-bottom: 1px solid var(--border);
}

.header h1 {
  font-size: 18px;
  font-weight: 600;
  color: var(--text-primary);
}

.header-actions {
  display: flex;
  gap: 8px;
}

.header-actions button {
  padding: 8px 16px;
  background: var(--bg-tertiary);
  color: var(--text-primary);
  border: 1px solid var(--border);
  border-radius: 6px;
  cursor: pointer;
  font-size: 13px;
  font-weight: 500;
  transition: all 0.2s;
}

.header-actions button:hover {
  background: var(--accent);
  border-color: var(--accent);
}

.main {
  display: flex;
  flex: 1;
  overflow: hidden;
}

.pane {
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.editor-pane {
  flex: 1;
  min-width: 300px;
  background: var(--bg-tertiary);
}

.preview-pane {
  flex: 1;
  min-width: 300px;
  background: var(--bg-secondary);
}

.pane-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 16px;
  background: var(--bg-secondary);
  border-bottom: 1px solid var(--border);
  font-size: 13px;
  font-weight: 500;
}

.pane-header .hint {
  color: var(--text-secondary);
  font-weight: 400;
}

#editor {
  flex: 1;
  width: 100%;
  padding: 16px;
  background: var(--bg-tertiary);
  color: var(--text-primary);
  border: none;
  outline: none;
  resize: none;
  font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
  font-size: 14px;
  line-height: 1.6;
}

#editor::placeholder {
  color: var(--text-secondary);
  opacity: 0.7;
}

.resizer {
  width: 6px;
  background: var(--border);
  cursor: col-resize;
  transition: background 0.2s;
}

.resizer:hover,
.resizer.active {
  background: var(--accent);
}

.preview-content {
  flex: 1;
  overflow: auto;
  padding: 20px;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  background: #ffffff;
}

.preview-content svg {
  max-width: 100%;
  height: auto;
}

.error-message {
  display: none;
  padding: 12px 16px;
  background: rgba(239, 68, 68, 0.1);
  border-top: 1px solid var(--error);
  color: var(--error);
  font-size: 13px;
  font-family: monospace;
  white-space: pre-wrap;
  max-height: 150px;
  overflow: auto;
}

.error-message.visible {
  display: block;
}

#status.error {
  color: var(--error);
}

#status.success {
  color: var(--success);
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--bg-tertiary);
}

::-webkit-scrollbar-thumb {
  background: var(--border);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--text-secondary);
}

/* Copy feedback */
.copy-feedback {
  position: fixed;
  bottom: 20px;
  right: 20px;
  padding: 12px 20px;
  background: var(--success);
  color: white;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 500;
  animation: fadeInOut 2s ease-in-out;
  z-index: 1000;
}

@keyframes fadeInOut {
  0% { opacity: 0; transform: translateY(10px); }
  15% { opacity: 1; transform: translateY(0); }
  85% { opacity: 1; transform: translateY(0); }
  100% { opacity: 0; transform: translateY(-10px); }
}

