python3 << 'EOF'
import json
from collections import defaultdict

with open('/tmp/all_pr_files.json', 'r') as f:
    all_files = json.load(f)

# Filter bin-server files
server_files = [f for f in all_files if f['filename'].startswith('bin-server/')]

# Organize by layer
layers = defaultdict(lambda: {'added': [], 'modified': [], 'removed': []})

for f in server_files:
    path = f['filename']

    # Determine layer
    if '/core/' in path:
        if '/configurations/' in path:
            layer = 'Core: Configurations'
        elif '/entity/' in path:
            layer = 'Core: Entity (Database Models)'
        elif '/repository/' in path:
            layer = 'Core: Repository (Data Access)'
        else:
            layer = 'Core: Other'
    elif '/manager/' in path:
        if '/dao/' in path:
            layer = 'Manager: DAO'
        elif '/mapper/' in path:
            layer = 'Manager: Mapper'
        elif '/service/' in path:
            layer = 'Manager: Service'
        elif '/workflow/' in path:
            layer = 'Manager: Workflow'
        else:
            layer = 'Manager: Other'
    elif '/egress/' in path:
        layer = 'Egress: External Clients'
    elif '/ingress/' in path:
        layer = 'Ingress: Controllers (API)'
    elif '/src/test/' in path:
        layer = 'Tests'
    else:
        layer = 'Root/Config'

    layers[layer][f['status']].append(path.split('/')[-1])

# Print organized view
print("=" * 100)
print("BIN-SERVER ARCHITECTURE LAYERS")
print("=" * 100)
print()

for layer in sorted(layers.keys()):
    data = layers[layer]
    total = len(data['added']) + len(data['modified']) + len(data['removed'])
    print(f"\n {layer} ({total} files)")

    if data['added']:
        print(f"    Added ({len(data['added'])})")
        for f in data['added'][:5]:
            print(f"       {f}")
        if len(data['added']) > 5:
            print(f"      ... and {len(data['added']) - 5} more")

    if data['modified']:
        print(f"    Modified ({len(data['modified'])})")
        for f in data['modified'][:3]:
            print(f"       {f}")

    if data['removed']:
        print(f"    Removed ({len(data['removed'])})")
        for f in data['removed'][:3]:
            print(f"       {f}")
        if len(data['removed']) > 3:
            print(f"      ... and {len(data['removed']) - 3} more")

print()
print("=" * 100)
EOF
