     1→// Package search provides search service implementation.
     2→// This file provides search-specific validation functions.
     3→package search
     4→
     5→import (
     6→	"fmt"
     7→	"time"
     8→
     9→	"flights/common"
    10→
    11→	searchv1 "flights/proto/gen/flight/search/v1"
    12→)
    13→
    14→// ValidateSearchFlightsRequest validates a search flights request.
    15→// This validates all input parameters including required fields, optional traveler counts, and filters.
    16→// Uses proto getters to safely access optional fields without nil checks.
    17→func ValidateSearchFlightsRequest(req *searchv1.SearchFlightsRequest) error {
    18→	// Use proto getters - they handle nil checks and return zero values if not set
    19→	sourceAirportCode := req.GetSourceAirportCode()
    20→	destinationAirportCode := req.GetDestinationAirportCode()
    21→	departureTimestamp := req.GetDepartureDate()
    22→
    23→	// Validate required fields
    24→	if err := common.ValidateAirportCode(sourceAirportCode); err != nil {
    25→		return fmt.Errorf("source_airport_code: %w", err)
    26→	}
    27→	if err := common.ValidateAirportCode(destinationAirportCode); err != nil {
    28→		return fmt.Errorf("destination_airport_code: %w", err)
    29→	}
    30→	if sourceAirportCode == destinationAirportCode {
    31→		return fmt.Errorf("source and destination airports cannot be the same: %s", sourceAirportCode)
    32→	}
    33→	
    34→	// Validate departure date timestamp
    35→	if departureTimestamp == nil {
    36→		return fmt.Errorf("departure_date: timestamp is required")
    37→	}
    38→	
    39→	// Convert timestamp to time.Time
    40→	depDate := departureTimestamp.AsTime()
    41→	
    42→	// Validate that departure date is not in the past
    43→	if depDate.Before(time.Now().Truncate(24 * time.Hour)) {
    44→		return fmt.Errorf("departure_date cannot be in the past: %s", depDate.Format("2006-01-02"))
    45→	}
    46→
    47→	// Validate traveler counts if provided (proto getter returns nil if not set)
    48→	if travelerCounts := req.GetTravelerCounts(); travelerCounts != nil {
    49→		if err := ValidateTravelerCounts(
    50→			travelerCounts.GetAdults(),
    51→			travelerCounts.GetChildren(),
    52→			travelerCounts.GetInfants(),
    53→		); err != nil {
    54→			return fmt.Errorf("traveler_counts: %w", err)
    55→		}
    56→	}
    57→
    58→	// Validate filters if provided (proto getter returns nil if not set)
    59→	if filters := req.GetFilters(); filters != nil {
    60→		// Validate price range
    61→		if priceRange := filters.GetPriceRange(); priceRange != nil {
    62→			if err := ValidateRangeFilter(priceRange.GetMin(), priceRange.GetMax(), "price"); err != nil {
    63→				return fmt.Errorf("filters.price_range: %w", err)
    64→			}
    65→		}
    66→
    67→		// Validate duration range
    68→		if durationRange := filters.GetDurationRange(); durationRange != nil {
    69→			if err := ValidateRangeFilter(durationRange.GetMin(), durationRange.GetMax(), "duration"); err != nil {
    70→				return fmt.Errorf("filters.duration_range: %w", err)
    71→			}
    72→		}
    73→
    74→		// Validate stops filter
    75→		if stops := filters.GetStops(); len(stops) > 0 {
    76→			if err := ValidateStopsFilter(stops); err != nil {
    77→				return fmt.Errorf("filters.stops: %w", err)
    78→			}
    79→		}
    80→	}
    81→
    82→	return nil
    83→}
    84→
    85→// ValidateTravelerCounts validates passenger counts.
    86→// At least one adult is required for a valid booking.
    87→func ValidateTravelerCounts(adults, children, infants int32) error {
    88→	if adults < 1 {
    89→		return fmt.Errorf("at least one adult passenger is required, got %d", adults)
    90→	}
    91→	if children < 0 {
    92→		return fmt.Errorf("children count cannot be negative, got %d", children)
    93→	}
    94→	if infants < 0 {
    95→		return fmt.Errorf("infants count cannot be negative, got %d", infants)
    96→	}
    97→	// Business rule: infants should not exceed adults (each infant needs an adult)
    98→	if infants > adults {
    99→		return fmt.Errorf("number of infants (%d) cannot exceed number of adults (%d)", infants, adults)
   100→	}
   101→	return nil
   102→}
   103→
   104→// ValidateRangeFilter validates a range filter (price or duration).
   105→// Min must be less than or equal to max, and both must be non-negative.
   106→func ValidateRangeFilter(min, max int64, filterType string) error {
   107→	if min < 0 {
   108→		return fmt.Errorf("%s range min cannot be negative, got %d", filterType, min)
   109→	}
   110→	if max < 0 {
   111→		return fmt.Errorf("%s range max cannot be negative, got %d", filterType, max)
   112→	}
   113→	if min > max {
   114→		return fmt.Errorf("%s range min (%d) cannot be greater than max (%d)", filterType, min, max)
   115→	}
   116→	return nil
   117→}
   118→
   119→// ValidateStopsFilter validates the stops filter values.
   120→// Stops must be non-negative integers.
   121→func ValidateStopsFilter(stops []int32) error {
   122→	for i, stop := range stops {
   123→		if stop < 0 {
   124→			return fmt.Errorf("stops[%d] cannot be negative, got %d", i, stop)
   125→		}
   126→	}
   127→	return nil
   128→}
   129→

