     1→// Package booking provides booking service implementation.
     2→// This file provides booking-specific validation functions.
     3→package booking
     4→
     5→import (
     6→	"fmt"
     7→	"regexp"
     8→	"strings"
     9→
    10→	"flights/booking/dao"
    11→	"flights/common"
    12→)
    13→
    14→// ValidatePassenger validates a Passenger struct.
    15→// Passengers must have valid names, email, phone, and date of birth.
    16→func ValidatePassenger(p *dao.Passenger) error {
    17→	if p == nil {
    18→		return fmt.Errorf("%w: passenger cannot be nil", common.ErrEmptyField)
    19→	}
    20→	if p.FirstName == "" {
    21→		return fmt.Errorf("%w: passenger first_name cannot be empty", common.ErrInvalidPassengerData)
    22→	}
    23→	if p.LastName == "" {
    24→		return fmt.Errorf("%w: passenger last_name cannot be empty", common.ErrInvalidPassengerData)
    25→	}
    26→	if err := common.ValidateEmail(p.Email); err != nil {
    27→		return fmt.Errorf("%w: passenger email: %v", common.ErrInvalidPassengerData, err)
    28→	}
    29→	if err := common.ValidatePhoneNumber(p.PhoneNumber); err != nil {
    30→		return fmt.Errorf("%w: passenger phone_number: %v", common.ErrInvalidPassengerData, err)
    31→	}
    32→	// Validate date of birth format (YYYY-MM-DD)
    33→	if err := common.ValidateDate(p.DateOfBirth); err != nil {
    34→		return fmt.Errorf("%w: passenger date_of_birth: %v", common.ErrInvalidPassengerData, err)
    35→	}
    36→	// Validate gender (optional but if provided should be valid)
    37→	if p.Gender != "" && p.Gender != "M" && p.Gender != "F" && p.Gender != "OTHER" {
    38→		return fmt.Errorf("%w: passenger gender must be M, F, or OTHER", common.ErrInvalidPassengerData)
    39→	}
    40→	return nil
    41→}
    42→
    43→// ValidatePassengers validates a slice of passengers.
    44→// At least one passenger is required for a booking.
    45→func ValidatePassengers(passengers []dao.Passenger) error {
    46→	if len(passengers) == 0 {
    47→		return common.ErrInvalidPassengers
    48→	}
    49→	for i, p := range passengers {
    50→		if err := ValidatePassenger(&p); err != nil {
    51→			return fmt.Errorf("passenger[%d]: %w", i, err)
    52→		}
    53→	}
    54→	return nil
    55→}
    56→
    57→// ValidateInitiateBookingRequest validates an initiate booking request.
    58→// This validates all required fields for creating a new booking.
    59→func ValidateInitiateBookingRequest(userID string, scheduleIDs []string, passengers []dao.Passenger, totalAmount *common.Money) error {
    60→	if err := ValidateUserID(userID); err != nil {
    61→		return fmt.Errorf("user_id: %w", err)
    62→	}
    63→	if err := ValidateScheduleIDs(scheduleIDs); err != nil {
    64→		return fmt.Errorf("schedule_ids: %w", err)
    65→	}
    66→	if err := ValidatePassengers(passengers); err != nil {
    67→		return fmt.Errorf("passengers: %w", err)
    68→	}
    69→	if err := common.ValidateMoney(totalAmount); err != nil {
    70→		return fmt.Errorf("total_amount: %w", err)
    71→	}
    72→	return nil
    73→}
    74→
    75→// ValidateGetBookingDetailsRequest validates a get booking details request.
    76→func ValidateGetBookingDetailsRequest(bookingID string) error {
    77→	return common.ValidateUUID(bookingID)
    78→}
    79→
    80→// ValidateProcessPaymentWebhookRequest validates a payment webhook request.
    81→func ValidateProcessPaymentWebhookRequest(paymentRefID, status string) error {
    82→	if err := ValidatePaymentRefID(paymentRefID); err != nil {
    83→		return fmt.Errorf("payment_ref_id: %w", err)
    84→	}
    85→	if err := ValidatePaymentStatus(status); err != nil {
    86→		return fmt.Errorf("status: %w", err)
    87→	}
    88→	return nil
    89→}
    90→
    91→// ValidateProcessVendorWebhookRequest validates a vendor webhook request.
    92→func ValidateProcessVendorWebhookRequest(vendorBookingID, status, pnr string) error {
    93→	if err := ValidateVendorBookingID(vendorBookingID); err != nil {
    94→		return fmt.Errorf("vendor_booking_id: %w", err)
    95→	}
    96→	if err := ValidateVendorStatus(status); err != nil {
    97→		return fmt.Errorf("status: %w", err)
    98→	}
    99→	// PNR is only required when status is CONFIRMED
   100→	if status == "CONFIRMED" {
   101→		if err := ValidatePNR(pnr); err != nil {
   102→			return fmt.Errorf("pnr: %w", err)
   103→		}
   104→	}
   105→	return nil
   106→}
   107→
   108→// ValidateScheduleIDs validates a slice of schedule IDs.
   109→// At least one schedule ID is required, and all must be valid UUIDs.
   110→func ValidateScheduleIDs(scheduleIDs []string) error {
   111→	if len(scheduleIDs) == 0 {
   112→		return common.ErrInvalidScheduleIDs
   113→	}
   114→	for i, id := range scheduleIDs {
   115→		if err := common.ValidateUUID(id); err != nil {
   116→			return fmt.Errorf("schedule_ids[%d]: %w", i, err)
   117→		}
   118→	}
   119→	return nil
   120→}
   121→
   122→// ValidatePaymentStatus validates a payment status string.
   123→// Payment status must be one of the allowed values.
   124→func ValidatePaymentStatus(status string) error {
   125→	validStatuses := map[string]bool{
   126→		"SUCCESS": true,
   127→		"FAILED":  true,
   128→		"PENDING": true,
   129→	}
   130→	if !validStatuses[status] {
   131→		return fmt.Errorf("%w: %s (must be SUCCESS, FAILED, or PENDING)", common.ErrInvalidStatus, status)
   132→	}
   133→	return nil
   134→}
   135→
   136→// ValidateVendorStatus validates a vendor status string.
   137→// Vendor status must be one of the allowed values.
   138→func ValidateVendorStatus(status string) error {
   139→	validStatuses := map[string]bool{
   140→		"CONFIRMED": true,
   141→		"REJECTED":  true,
   142→		"PENDING":   true,
   143→	}
   144→	if !validStatuses[status] {
   145→		return fmt.Errorf("%w: %s (must be CONFIRMED, REJECTED, or PENDING)", common.ErrInvalidStatus, status)
   146→	}
   147→	return nil
   148→}
   149→
   150→// ValidatePNR validates a PNR (Passenger Name Record) string.
   151→// PNR is typically 6 alphanumeric characters, but we allow flexible format.
   152→func ValidatePNR(pnr string) error {
   153→	if pnr == "" {
   154→		return fmt.Errorf("%w: PNR cannot be empty", common.ErrEmptyField)
   155→	}
   156→	// PNR is typically 6 characters, but some systems use longer codes
   157→	if len(pnr) < 3 || len(pnr) > 20 {
   158→		return fmt.Errorf("PNR must be between 3 and 20 characters: %s", pnr)
   159→	}
   160→	// PNR should be alphanumeric
   161→	pnrRegex := regexp.MustCompile(`^[A-Z0-9]+$`)
   162→	if !pnrRegex.MatchString(strings.ToUpper(pnr)) {
   163→		return fmt.Errorf("PNR must contain only alphanumeric characters: %s", pnr)
   164→	}
   165→	return nil
   166→}
   167→
   168→// ValidateUserID validates a user ID string.
   169→// User ID can be a UUID or any non-empty string (depending on auth system).
   170→func ValidateUserID(userID string) error {
   171→	if userID == "" {
   172→		return fmt.Errorf("%w: user_id cannot be empty", common.ErrEmptyField)
   173→	}
   174→	// User ID can be UUID or other format - we just check it's not empty
   175→	// In production, you might want stricter validation based on your auth system
