// Package booking provides booking service implementation.
// This file provides booking-specific validation functions.
package booking

import (
	"fmt"
	"regexp"
	"strings"

	"flights/booking/dao"
	"flights/common"
)

// ValidatePassenger validates a Passenger struct.
// Passengers must have valid names, email, phone, and date of birth.
func ValidatePassenger(p *dao.Passenger) error {
	if p == nil {
		return fmt.Errorf("%w: passenger cannot be nil", common.ErrEmptyField)
	}
	if p.FirstName == "" {
		return fmt.Errorf("%w: passenger first_name cannot be empty", common.ErrInvalidPassengerData)
	}
	if p.LastName == "" {
		return fmt.Errorf("%w: passenger last_name cannot be empty", common.ErrInvalidPassengerData)
	}
	if err := common.ValidateEmail(p.Email); err != nil {
		return fmt.Errorf("%w: passenger email: %v", common.ErrInvalidPassengerData, err)
	}
	if err := common.ValidatePhoneNumber(p.PhoneNumber); err != nil {
		return fmt.Errorf("%w: passenger phone_number: %v", common.ErrInvalidPassengerData, err)
	}
	// Validate date of birth format (YYYY-MM-DD)
	if err := common.ValidateDate(p.DateOfBirth); err != nil {
		return fmt.Errorf("%w: passenger date_of_birth: %v", common.ErrInvalidPassengerData, err)
	}
	// Validate gender (optional but if provided should be valid)
	if p.Gender != "" && p.Gender != "M" && p.Gender != "F" && p.Gender != "OTHER" {
		return fmt.Errorf("%w: passenger gender must be M, F, or OTHER", common.ErrInvalidPassengerData)
	}
	return nil
}

// ValidatePassengers validates a slice of passengers.
// At least one passenger is required for a booking.
func ValidatePassengers(passengers []dao.Passenger) error {
	if len(passengers) == 0 {
		return common.ErrInvalidPassengers
	}
	for i, p := range passengers {
		if err := ValidatePassenger(&p); err != nil {
			return fmt.Errorf("passenger[%d]: %w", i, err)
		}
	}
	return nil
}

// ValidateInitiateBookingRequest validates an initiate booking request.
// This validates all required fields for creating a new booking.
func ValidateInitiateBookingRequest(userID string, scheduleIDs []string, passengers []dao.Passenger, totalAmount *common.Money) error {
	if err := ValidateUserID(userID); err != nil {
		return fmt.Errorf("user_id: %w", err)
	}
	if err := ValidateScheduleIDs(scheduleIDs); err != nil {
		return fmt.Errorf("schedule_ids: %w", err)
	}
	if err := ValidatePassengers(passengers); err != nil {
		return fmt.Errorf("passengers: %w", err)
	}
	if err := common.ValidateMoney(totalAmount); err != nil {
		return fmt.Errorf("total_amount: %w", err)
	}
	return nil
}

// ValidateGetBookingDetailsRequest validates a get booking details request.
func ValidateGetBookingDetailsRequest(bookingID string) error {
	return common.ValidateUUID(bookingID)
}

// ValidateProcessPaymentWebhookRequest validates a payment webhook request.
func ValidateProcessPaymentWebhookRequest(paymentRefID, status string) error {
	if err := ValidatePaymentRefID(paymentRefID); err != nil {
		return fmt.Errorf("payment_ref_id: %w", err)
	}
	if err := ValidatePaymentStatus(status); err != nil {
		return fmt.Errorf("status: %w", err)
	}
	return nil
}

// ValidateProcessVendorWebhookRequest validates a vendor webhook request.
func ValidateProcessVendorWebhookRequest(vendorBookingID, status, pnr string) error {
	if err := ValidateVendorBookingID(vendorBookingID); err != nil {
		return fmt.Errorf("vendor_booking_id: %w", err)
	}
	if err := ValidateVendorStatus(status); err != nil {
		return fmt.Errorf("status: %w", err)
	}
	// PNR is only required when status is CONFIRMED
	if status == "CONFIRMED" {
		if err := ValidatePNR(pnr); err != nil {
			return fmt.Errorf("pnr: %w", err)
		}
	}
	return nil
}

// ValidateScheduleIDs validates a slice of schedule IDs.
// At least one schedule ID is required, and all must be valid UUIDs.
func ValidateScheduleIDs(scheduleIDs []string) error {
	if len(scheduleIDs) == 0 {
		return common.ErrInvalidScheduleIDs
	}
	for i, id := range scheduleIDs {
		if err := common.ValidateUUID(id); err != nil {
			return fmt.Errorf("schedule_ids[%d]: %w", i, err)
		}
	}
	return nil
}

// ValidatePaymentStatus validates a payment status string.
// Payment status must be one of the allowed values.
func ValidatePaymentStatus(status string) error {
	validStatuses := map[string]bool{
		"SUCCESS": true,
		"FAILED":  true,
		"PENDING": true,
	}
	if !validStatuses[status] {
		return fmt.Errorf("%w: %s (must be SUCCESS, FAILED, or PENDING)", common.ErrInvalidStatus, status)
	}
	return nil
}

// ValidateVendorStatus validates a vendor status string.
// Vendor status must be one of the allowed values.
func ValidateVendorStatus(status string) error {
	validStatuses := map[string]bool{
		"CONFIRMED": true,
		"REJECTED":  true,
		"PENDING":   true,
	}
	if !validStatuses[status] {
		return fmt.Errorf("%w: %s (must be CONFIRMED, REJECTED, or PENDING)", common.ErrInvalidStatus, status)
	}
	return nil
}

// ValidatePNR validates a PNR (Passenger Name Record) string.
// PNR is typically 6 alphanumeric characters, but we allow flexible format.
func ValidatePNR(pnr string) error {
	if pnr == "" {
		return fmt.Errorf("%w: PNR cannot be empty", common.ErrEmptyField)
	}
	// PNR is typically 6 characters, but some systems use longer codes
	if len(pnr) < 3 || len(pnr) > 20 {
		return fmt.Errorf("PNR must be between 3 and 20 characters: %s", pnr)
	}
	// PNR should be alphanumeric
	pnrRegex := regexp.MustCompile(`^[A-Z0-9]+$`)
	if !pnrRegex.MatchString(strings.ToUpper(pnr)) {
		return fmt.Errorf("PNR must contain only alphanumeric characters: %s", pnr)
	}
	return nil
}

// ValidateUserID validates a user ID string.
// User ID can be a UUID or any non-empty string (depending on auth system).
func ValidateUserID(userID string) error {
	if userID == "" {
		return fmt.Errorf("%w: user_id cannot be empty", common.ErrEmptyField)
	}
	// User ID can be UUID or other format - we just check it's not empty
	// In production, you might want stricter validation based on your auth system
	return nil
}

// ValidatePaymentRefID validates a payment reference ID string.
// Payment reference IDs are provided by payment gateways and should not be empty.
func ValidatePaymentRefID(paymentRefID string) error {
	if paymentRefID == "" {
		return fmt.Errorf("%w: payment_ref_id cannot be empty", common.ErrEmptyField)
	}
	// Payment reference IDs can have various formats depending on the gateway
	// We just ensure it's not empty and has reasonable length
	if len(paymentRefID) > 255 {
		return fmt.Errorf("payment_ref_id must be 255 characters or less: %s", paymentRefID)
	}
	return nil
}

// ValidateVendorBookingID validates a vendor booking ID string.
// Vendor booking IDs are provided by vendor platforms and should not be empty.
func ValidateVendorBookingID(vendorBookingID string) error {
	if vendorBookingID == "" {
		return fmt.Errorf("%w: vendor_booking_id cannot be empty", common.ErrEmptyField)
	}
	// Vendor booking IDs can have various formats depending on the vendor
	// We just ensure it's not empty and has reasonable length
	if len(vendorBookingID) > 255 {
		return fmt.Errorf("vendor_booking_id must be 255 characters or less: %s", vendorBookingID)
	}
	return nil
}

package booking

import (
	"context"
	"testing"
	"time"

	"flights/booking"
	"flights/booking/dao"
	"flights/common"
	"flights/database"
	"flights/external/payment"
	"flights/external/refund"
	"flights/external/vendor"
	searchdao "flights/search/dao"

	bookingv1 "flights/proto/gen/flight/booking/v1"
	searchv1 "flights/proto/gen/flight/search/v1"
	"google.golang.org/genproto/googleapis/type/money"
)

// TestService_InitiateBooking tests the BookingService InitiateBooking gRPC method.
func TestService_InitiateBooking(t *testing.T) {
	// Set environment variables for database connections
	t.Setenv("FLIGHT_DB_HOST", "localhost")
	t.Setenv("FLIGHT_DB_PORT", "5434")
	t.Setenv("FLIGHT_DB_USER", "flight_user")
	t.Setenv("FLIGHT_DB_PASSWORD", "flight_pass")
	t.Setenv("FLIGHT_DB_NAME", "flight_db")

	if err := database.InitFlightDB(); err != nil {
		t.Fatalf("Failed to initialize Flight DB: %v", err)
	}
	defer database.CloseFlightDB()

	if err := database.InitBookingDB(); err != nil {
		t.Fatalf("Failed to initialize Booking DB: %v", err)
	}
	defer database.CloseBookingDB()

	// Initialize dependencies
	bookingDAO := dao.NewDAO(database.GetBookingGormDB())
	searchDAO := searchdao.NewDAO(database.GetFlightGormDB())
	lockManager := common.NewLockManager(database.GetFlightDB())
	workflow := booking.NewWorkflow(bookingDAO, lockManager)

	// Create Stub clients for external dependencies
	paymentClient := payment.NewStubClient()
	vendorClient := vendor.NewStubClient()
	refundClient := refund.NewStubClient()

	svc := booking.NewBookingService(bookingDAO, searchDAO, workflow, paymentClient, vendorClient, refundClient)
	ctx := context.Background()

	// Create test schedules in Flight DB if needed, or mock validation
	// For integration tests, we need real data in Flight DB for validation to pass
	// Pre-seed a schedule or ensure validation skips if missing? 
	// The validation checks for existence. Let's create a schedule in Flight DB first.
	scheduleID := generateUUID()
	flightNumber := "FL-123"

	// Create schedule in DB using searchDAO (we might need a method or raw SQL if not exposed)
	// Since searchDAO is read-only for search service, we might need a workaround or just assume validation relies on it.
	// Actually, `searchDAO.GetScheduleByID` is used. We need to insert data.
	// For simplicity in this test environment, we can insert directly via GORM or SQL.
	flightDB := database.GetFlightGormDB()

	// Ensure flight master exists
	flightDB.Exec("INSERT INTO flights (flight_number, model, airline, total_seats, source_airport_code, destination_airport_code) VALUES (?, ?, ?, ?, ?, ?) ON CONFLICT DO NOTHING", 
		flightNumber, "Boeing 737", "Test Air", 180, "SFO", "JFK")

	// Insert schedule
	// NOTE: current_price is JSONB, not separate columns. 
	// Using hardcoded JSON for price: {"currency_code": "USD", "units": 299, "nanos": 0}
	flightDB.Exec("INSERT INTO flight_schedules (id, flight_number, from_time, to_time, current_price, available_seats, status) VALUES (?, ?, ?, ?, ?, ?, ?)",
		scheduleID, flightNumber, time.Now().Add(24*time.Hour), time.Now().Add(28*time.Hour), `{"currency_code": "USD", "units": 299, "nanos": 0}`, 100, "SCHEDULED")

	defer flightDB.Exec("DELETE FROM flight_schedules WHERE id = ?", scheduleID)

	// Create Request
	req := &bookingv1.InitiateBookingRequest{
		UserId:      "user-test-service",
		ScheduleIds: []string{scheduleID},
		Passengers: []*bookingv1.PassengerDetails{
			{
				FirstName:   "Alice",
				LastName:    "Test",
				Email:       "alice@example.com",
				PhoneNumber: "+15551234567",
				DateOfBirth: "1990-01-01",
				Gender:      "F",
			},
		},
		FinalPriceShown: &money.Money{
			CurrencyCode: "USD",
			Units:        299,
			Nanos:        0,
		},
	}

	// Call Service
	resp, err := svc.InitiateBooking(ctx, req)
	if err != nil {
		t.Fatalf("InitiateBooking failed: %v", err)
	}

	// Verify Response
	if !resp.Status.Success {
		t.Errorf("Expected success, got failure: %s", resp.Status.Message)
	}
	if resp.Booking == nil {
		t.Fatal("Expected booking in response")
	}
	if resp.Booking.Status != bookingv1.BookingStatus_IN_PROGRESS {
		t.Errorf("Expected status IN_PROGRESS, got %s", resp.Booking.Status)
	}
}

// TestService_ValidateItinerary tests ValidateItinerary gRPC method
func TestService_ValidateItinerary(t *testing.T) {
	// Setup similar to above...
	t.Setenv("FLIGHT_DB_HOST", "localhost")
	t.Setenv("FLIGHT_DB_PORT", "5434")
	t.Setenv("FLIGHT_DB_USER", "flight_user")
	t.Setenv("FLIGHT_DB_PASSWORD", "flight_pass")
	t.Setenv("FLIGHT_DB_NAME", "flight_db")

	if err := database.InitFlightDB(); err != nil {
		t.Fatalf("Failed to initialize Flight DB: %v", err)
	}
	defer database.CloseFlightDB()

	// We only need searchDAO for validation
	searchDAO := searchdao.NewDAO(database.GetFlightGormDB())
	// Use nil for other deps as they shouldn't be used in ValidateItinerary
	svc := booking.NewBookingService(nil, searchDAO, nil, nil, nil, nil)
	ctx := context.Background()

	// Seed data
	scheduleID := generateUUID()
	flightNumber := "FL-VALIDATE"
	flightDB := database.GetFlightGormDB()
	flightDB.Exec("INSERT INTO flights (flight_number, model, airline, total_seats, source_airport_code, destination_airport_code) VALUES (?, ?, ?, ?, ?, ?) ON CONFLICT DO NOTHING", 
		flightNumber, "A320", "Test Air", 150, "LAX", "NYC")
	flightDB.Exec("INSERT INTO flight_schedules (id, flight_number, from_time, to_time, current_price, available_seats, status) VALUES (?, ?, ?, ?, ?, ?, ?)",
		scheduleID, flightNumber, time.Now().Add(48*time.Hour), time.Now().Add(53*time.Hour), `{"currency_code": "USD", "units": 199, "nanos": 0}`, 50, "SCHEDULED")
	defer flightDB.Exec("DELETE FROM flight_schedules WHERE id = ?", scheduleID)

	// Create Request
	price := &money.Money{CurrencyCode: "USD", Units: 199, Nanos: 0}
	req := &bookingv1.ValidateItineraryRequest{
		Itinerary: &searchv1.FlightItinerary{
			FlightLegs: []*searchv1.FlightLeg{
				{
					Schedule: &searchv1.FlightSchedule{
						Id: scheduleID,
						CurrentPrice: price, 
					},
				},
			},
			TotalPrice: price,
		},
	}

	resp, err := svc.ValidateItinerary(ctx, req)
	if err != nil {
		t.Fatalf("ValidateItinerary failed: %v", err)
	}

	if !resp.Status.Success {
		t.Errorf("Expected success, got %s", resp.Status.Message)
	}
	if !resp.IsAvailable {
		t.Errorf("Expected IsAvailable true")
	}
}

