// Package common provides shared utilities used across services.
// This file provides the Money type that wraps google.type.Money for database storage.
// We use a type alias for money.Money that implements database interfaces (Value/Scan)
// to enable JSONB storage in PostgreSQL while using the protobuf Money type directly.
package common

import (
	"database/sql/driver"
	"encoding/json"
	"fmt"

	"google.golang.org/genproto/googleapis/type/money"
)

// Money is a type alias for money.Money that implements database interfaces.
// This allows direct use of google.type.Money with JSONB storage in PostgreSQL.
// The type alias preserves all fields and methods from money.Money while adding
// database serialization capabilities.
type Money money.Money

// ToProto converts Money to *money.Money.
// This is used when converting from database storage to protobuf types.
func (m *Money) ToProto() *money.Money {
	if m == nil {
		return nil
	}
	return (*money.Money)(m)
}

// FromProto creates Money from *money.Money.
// This is used when converting from protobuf types to database storage.
func MoneyFromProto(m *money.Money) *Money {
	if m == nil {
		return nil
	}
	return (*Money)(m)
}

// Value implements driver.Valuer for database writes.
// This converts Money to JSONB format for PostgreSQL storage.
// We use snake_case keys (currency_code, units, nanos) to match database conventions.
// GORM requires this to work with both value and pointer types.
func (m Money) Value() (driver.Value, error) {
	// Convert to JSON with snake_case keys for database storage
	return json.Marshal(map[string]interface{}{
		"currency_code": m.CurrencyCode,
		"units":         m.Units,
		"nanos":         m.Nanos,
	})
}

// Scan implements sql.Scanner for database reads.
// This converts JSONB from PostgreSQL back to Money type.
// We handle multiple numeric types (float64, int64, int) because JSON unmarshaling
// can produce different types depending on the value size.
func (m *Money) Scan(value interface{}) error {
	if value == nil {
		if m != nil {
			*m = Money{}
		}
		return nil
	}

	var bytes []byte
	switch v := value.(type) {
	case []byte:
		bytes = v
	case string:
		bytes = []byte(v)
	default:
		return fmt.Errorf("failed to unmarshal money value: %v", value)
	}

	// Parse JSON with snake_case keys
	var data map[string]interface{}
	if err := json.Unmarshal(bytes, &data); err != nil {
		return err
	}

	if m == nil {
		m = &Money{}
	}

	if cc, ok := data["currency_code"].(string); ok {
		m.CurrencyCode = cc
	}
	if u, ok := data["units"].(float64); ok {
		m.Units = int64(u)
	} else if u, ok := data["units"].(int64); ok {
		m.Units = u
	} else if u, ok := data["units"].(int); ok {
		m.Units = int64(u)
	}
	if n, ok := data["nanos"].(float64); ok {
		m.Nanos = int32(n)
	} else if n, ok := data["nanos"].(int32); ok {
		m.Nanos = n
	} else if n, ok := data["nanos"].(int); ok {
		m.Nanos = int32(n)
	}

	return nil
}

