	Schedule *VendorSchedule `json:"schedule"`

	// Flight master data - may be included if flight information changed
	Flight *VendorFlight `json:"flight,omitempty"`

	// Schedule ID - vendor's unique identifier for this schedule
	ScheduleID string `json:"schedule_id,omitempty"`

	// Timestamp when the event occurred
	EventTimestamp time.Time `json:"event_timestamp"`
}

// VendorFlight represents flight master data from the vendor.
type VendorFlight struct {
	FlightNumber          string    `json:"flight_number"`
	Model                 string    `json:"model"`
	Airline               string    `json:"airline"`
	TotalSeats            int       `json:"total_seats"`
	SourceAirportCode     string    `json:"source_airport_code"`
