}

// ProcessScheduleWebhook processes a schedule update webhook from the vendor.
// This method:
// 1. Validates the webhook payload
// 2. Updates or inserts flight master data if provided
// 3. Updates or inserts schedule data based on event type
// 4. Handles errors gracefully (logs but doesn't fail the webhook)
func (s *WebhookService) ProcessScheduleWebhook(ctx context.Context, payload *ScheduleUpdateWebhookPayload) error {
